mapper.update:
UPDATE assess_cycle
SET cycle_state = '1' WHERE (cycle_state = '2' OR cycle_state = '4')
AND id = #{_parameter}

UPDATE
assess_cycle SET cycle_state = '3' WHERE cycle_state != '2' AND id =
#{_parameter}

mapper.select:
SELECT id AS id, unit_id AS unitId, cycle_name AS cycleName, cycle_start_time AS cycleStartTime, cycle_end_time AS cycleEndTime, assess_start_time AS assessStartTime, assess_end_time AS assessEndTime, cycle_state AS cycleState, assess_type AS assessType, cycle_describe AS cycleDescribe, create_time AS createTime, create_user_id AS createUserId, update_time AS updateTime, update_user_id AS updateUserId FROM assess_cycle WHERE cycle_state = '1'
AND unit_id = #{_parameter}
ORDER BY create_time DESC LIMIT 1
