SELECT ou.`id` AS id, ou.`name` , ou.org_agency_id AS agencyId, ou.org_unit_id AS unitId, cy.self_ass_grade AS selfAssGrade, cy.self_ass_time AS selfAssTime, cy.depart_grade AS departAssGrade, cy.depart_ass_time AS departAssTime, cy.unit_ass_grade AS unitAssGrade, cy.unit_ass_time AS unitAssTime, cy.confirmed_result AS confirmedResult, cy.confirmed_time AS confirmedTime, cy.operate_state AS operateState, cy.assess_cycle_id AS assessCycleId, COUNT(cy.unit_ass_grade IS NOT NULL OR NULL) AS unitAssGradeCOUNT, COUNT(cy.unit_ass_grade IS NULL OR NULL) AS notUnitAssGradeCOUNT, COUNT(ou.`org_unit_id`) AS total FROM org_user ou LEFT JOIN person_cyc_assess cy ON cy.org_user_id=ou.id AND assess_cycle_id = #{param.assessCycleId} WHERE ou.is_delete=0 AND ou.is_admin=0
and ou.org_unit_id=#{unitParam.unitId}
and ou.org_assess_team_id=#{unitParam.assessTeamId}
and ou.org_unit_id in
#{item}
and ou.org_agency_id=#{unitParam.agencyId}
and ou.org_agency_id in
#{item}
AND cy.self_ass_grade = #{param.selfAssGrade}
AND cy.depart_grade = #{param.departAssGrade}
AND cy.unit_ass_grade = #{param.unitAssGrade}
GROUP BY ou.`org_agency_id` ORDER BY ou.org_agency_id desc

