SELECT ou.`id` AS id, ou.`name` , ou.org_agency_id AS agencyId, ou.org_unit_id AS unitId, cy.self_ass_grade AS selfAssGrade, cy.self_ass_time AS selfAssTime, cy.depart_grade AS departAssGrade, cy.depart_ass_time AS departAssTime, cy.unit_ass_grade AS unitAssGrade, cy.unit_ass_time AS unitAssTime, cy.confirmed_result AS confirmedResult, cy.confirmed_time AS confirmedTime, cy.operate_state AS operateState, cy.assess_cycle_id AS assessCycleId FROM org_user ou LEFT JOIN person_cyc_assess cy ON cy.org_user_id=ou.id AND assess_cycle_id = #{assessCycleId} WHERE ou.is_delete=0 AND ou.is_admin=0 AND ou.org_unit_id=#{unitId}
AND ou.`name` like concat('%',#{name},'%')
ORDER BY cy.unit_ass_grade

